def words = ["fatther", "motherrr", "сенхрофазотрон"]
def correctedWords = ["father", "mother", "синхрофазотрон"]
int i =0;
for(word in words) {
		testRunner.testCase.setPropertyValue("word", word)
		testRunner.testCase.setPropertyValue("correctWord", correctedWords[i])
		testRunner.runTestStepByName("checkText")
		i++;
}

def langs = ["ch", "xs", "mx","ru"]
for(lang in langs) {	
		testRunner.testCase.setPropertyValue("lang", lang)
		testRunner.runTestStepByName("checkText")
}

def words = ["fatther", "helo", "сенхрофазотрон"]
